if (targetCase == "upper")
{
    System.log("Target Case is *upper*");
    var transformedString = input.toUpperCase();
}
else if (targetCase == "lower")
{
    System.log("Target Case is *lower*");
    var transformedString = input.toLowerCase();
}
else
{
    System.log("We received an unknown target case");
}
